    private static ToIntFunction<BlockState> createLightLevelFromLitBlockState(int litLevel) {
        return state -> state.get(Properties.LIT) != false ? litLevel : 0;
    }

    private static Boolean never(BlockState state, BlockView world, BlockPos pos, EntityType<?> type) {
        return false;
    }

    private static Boolean always(BlockState state, BlockView world, BlockPos pos, EntityType<?> type) {
        return true;
    }

    private static Boolean canSpawnOnLeaves(BlockState state, BlockView world, BlockPos pos, EntityType<?> type) {
        return type == EntityType.OCELOT || type == EntityType.PARROT;
    }

    private static BedBlock createBedBlock(DyeColor color) {
        return new BedBlock(color, AbstractBlock.Settings.of(Material.WOOL, state -> state.get(BedBlock.PART) == BedPart.FOOT ? color.getMapColor() : MapColor.WHITE_GRAY).sounds(BlockSoundGroup.WOOD).strength(0.2f).nonOpaque());
    }

    private static PillarBlock createLogBlock(MapColor topMapColor, MapColor sideMapColor) {
        return new PillarBlock(AbstractBlock.Settings.of(Material.WOOD, state -> state.get(PillarBlock.AXIS) == Direction.Axis.Y ? topMapColor : sideMapColor).strength(2.0f).sounds(BlockSoundGroup.WOOD));
    }

    private static PillarBlock createBambooBlock(MapColor topMapColor, MapColor sideMapColor, BlockSoundGroup soundGroup, FeatureFlag ... requiredFeatures) {
        return new PillarBlock(AbstractBlock.Settings.of(Material.WOOD, state -> state.get(PillarBlock.AXIS) == Direction.Axis.Y ? topMapColor : sideMapColor).strength(2.0f).sounds(soundGroup).requires(requiredFeatures));
    }

    private static Block createNetherStemBlock(MapColor mapColor) {
        return new PillarBlock(AbstractBlock.Settings.of(Material.NETHER_WOOD, state -> mapColor).strength(2.0f).sounds(BlockSoundGroup.NETHER_STEM));
    }

    private static boolean always(BlockState state, BlockView world, BlockPos pos) {
        return true;
    }

    private static boolean never(BlockState state, BlockView world, BlockPos pos) {
        return false;
    }

    private static StainedGlassBlock createStainedGlassBlock(DyeColor color) {
        return new StainedGlassBlock(color, AbstractBlock.Settings.of(Material.GLASS, color).strength(0.3f).sounds(BlockSoundGroup.GLASS).nonOpaque().allowsSpawning(Blocks::never).solidBlock(Blocks::never).suffocates(Blocks::never).blockVision(Blocks::never));
    }

    private static LeavesBlock createLeavesBlock(BlockSoundGroup soundGroup) {
        return new LeavesBlock(AbstractBlock.Settings.of(Material.LEAVES).strength(0.2f).ticksRandomly().sounds(soundGroup).nonOpaque().allowsSpawning(Blocks::canSpawnOnLeaves).suffocates(Blocks::never).blockVision(Blocks::never));
    }

    private static ShulkerBoxBlock createShulkerBoxBlock(DyeColor color, AbstractBlock.Settings settings) {
        AbstractBlock.ContextPredicate lv = (state, world, pos) -> {
            BlockEntity lv = world.getBlockEntity(pos);
            if (!(lv instanceof ShulkerBoxBlockEntity)) {
                return true;
            }
            ShulkerBoxBlockEntity lv2 = (ShulkerBoxBlockEntity)lv;
            return lv2.suffocates();
        };
        return new ShulkerBoxBlock(color, settings.strength(2.0f).dynamicBounds().nonOpaque().suffocates(lv).blockVision(lv));
    }

    private static PistonBlock createPistonBlock(boolean sticky) {
        AbstractBlock.ContextPredicate lv = (state, world, pos) -> state.get(PistonBlock.EXTENDED) == false;
        return new PistonBlock(sticky, AbstractBlock.Settings.of(Material.PISTON).strength(1.5f).solidBlock(Blocks::never).suffocates(lv).blockVision(lv));
    }

    private static ButtonBlock createWoodenButtonBlock(BlockSetType blockSetType) {
        return new ButtonBlock(AbstractBlock.Settings.of(Material.DECORATION).noCollision().strength(0.5f), blockSetType, 30, true);
    }

    private static ButtonBlock createStoneButtonBlock() {
        return new ButtonBlock(AbstractBlock.Settings.of(Material.DECORATION).noCollision().strength(0.5f), BlockSetType.STONE, 20, false);
    }

    private static Block register(String id, Block block) {
        return Registry.register(Registries.BLOCK, id, block);
    }

    public static void refreshShapeCache() {
        Block.STATE_IDS.forEach(AbstractBlock.AbstractBlockState::initShapeCache);
    }

    static {
        for (Block lv : Registries.BLOCK) {
            for (BlockState lv2 : lv.getStateManager().getStates()) {
                Block.STATE_IDS.add(lv2);
                lv2.initShapeCache();
            }
            lv.getLootTableId();
        }
    }
